import { ethers } from 'ethers';
import * as dotenv from 'dotenv';

dotenv.config();

// Simplified ERC20 Mock Contract (for testing only)
const ERC20_BYTECODE = ""+"";

const POLICY_MANAGER_BYTECODE = "";

async function deployContracts() {
  console.log('ðŸš€ Direct contract deployment to Shardeum Unstablenet');
  
  if (!process.env.PRIVATE_KEY) {
    console.error('âŒ Private key not found');
    process.exit(1);
  }

  // Connect to Shardeum Unstablenet
  const provider = new ethers.providers.JsonRpcProvider('https://api-testnet.shardeum.org/');
  const wallet = new ethers.Wallet(`0x${process.env.PRIVATE_KEY}`, provider);
  
  console.log('ðŸ“‹ Deployer:', wallet.address);
  
  const balance = await provider.getBalance(wallet.address);
  console.log('ðŸ’° Balance:', ethers.utils.formatEther(balance), 'SHM');

  if (balance.lt(ethers.utils.parseEther('0.1'))) {
    console.error('âŒ Insufficient balance for deployment');
    process.exit(1);
  }

  try {
    console.log('\nðŸ“¦ Deploying ERC20Mock token...');
    
    // Deploy ERC20Mock with simplified bytecode
    const tokenFactory = new ethers.ContractFactory(
      ["constructor(string memory name, string memory symbol, uint256 initialSupply)"],
      ERC20_BYTECODE,
      wallet
    );
    
    const token = await tokenFactory.deploy(
      "Test SHM Token",
      "tSHM", 
      ethers.utils.parseUnits("1000000", 18),
      {
        gasLimit: 2000000,
        gasPrice: ethers.utils.parseUnits("20", "gwei")
      }
    );
    
    console.log('â³ Waiting for token deployment...');
    await token.deployed();
    console.log('âœ… ERC20Mock deployed at:', token.address);

    console.log('\nðŸ“¦ Deploying PolicyManager...');
    
    const policyFactory = new ethers.ContractFactory(
      ["constructor(address tokenAddress, address companyWallet)"],
      POLICY_MANAGER_BYTECODE,
      wallet
    );
    
    const policyManager = await policyFactory.deploy(
      token.address,
      wallet.address,
      {
        gasLimit: 2000000,
        gasPrice: ethers.utils.parseUnits("20", "gwei")
      }
    );
    
    console.log('â³ Waiting for PolicyManager deployment...');
    await policyManager.deployed();
    console.log('âœ… PolicyManager deployed at:', policyManager.address);

    console.log('\nðŸŽ‰ Deployment completed successfully!');
    console.log('ðŸ“‹ Contract Addresses:');
    console.log('   ERC20Mock Token:', token.address);
    console.log('   PolicyManager:', policyManager.address);
    console.log('\nðŸ“ Update your .env file with these addresses:');
    console.log(`DEPLOYED_TOKEN_ADDRESS=${token.address}`);
    console.log(`DEPLOYED_POLICY_MANAGER_ADDRESS=${policyManager.address}`);
    
    return {
      tokenAddress: token.address,
      policyManagerAddress: policyManager.address
    };

  } catch (error) {
    console.error('âŒ Deployment failed:', error.message);
    
    // Fallback: Use Remix IDE
    console.log('\nðŸŒ Alternative: Deploy via Remix IDE');
    console.log('1. Go to https://remix.ethereum.org/');
    console.log('2. Upload contract files from ./contracts/');
    console.log('3. Compile contracts');
    console.log('4. Connect MetaMask to Shardeum Unstablenet');
    console.log('5. Deploy manually');
    
    process.exit(1);
  }
}

deployContracts()
  .then(() => process.exit(0))
  .catch((error) => {
    console.error(error);
    process.exit(1);
  });
